import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useState } from 'react';
import axios from 'axios';

const url = "http://localhost:3001/addInventario"

function FormAdd() {

    const producto = {
        id: '13',
        descripcion: 's',
        precio: 's'
    }

    const add = async (e) => {
        e.preventDefault()
        try {
            const resp = await axios.post(url, producto);
            console.log(resp.data);
            console.log(resp.data.value)
        } catch (err) {
            // Handle Error Here
            console.log(err);
        }
    }

        return (

            <div className=" mx-auto mt-5" style={{width: "30rem"}}>
                <div className="card-body p-5 ">
                    <br />
                    <h3 className="card-title text-info mx-5"><b>Agregar Un Producto</b></h3><br />
                    <form className="form-group" onSubmit={add}>
                        <label className="ml-2">C贸digo del nuevo producto:</label>
                        <p><input type="text" placeholder="C贸digo" className="form-control " name="codigo" ></input></p>
                        <label className="ml-2">Descripci贸n del nuevo producto</label>
                        <p><input type="text" placeholder="Descripci贸n" className="form-control" name="descripcion"  ></input></p>
                        <label className="ml-2">Precio del nuevo producto:</label>
                        <p><input type="text" placeholder="Precio" className="form-control" name="precio"  ></input></p>
                        <br />
                        <button type="submit" className="btn btn-success float-left px-5">Agregar</button> 
                        <a href="/getInventario"  className="btn btn-danger float-right px-5">Cancelar</a>
                    </form>
                </div>
            </div>
        );
};

export default FormAdd;
