import 'bootstrap/dist/css/bootstrap.min.css';

import { Component, useEffect, useState } from 'react';
import axios from 'axios';
import { useModal } from '../Hooks/useModal';
import FormEdit from './FormEdit';

const url = "http://localhost:3001/getInventario"

const TablaMateriales = () => {

  const [isOpenModal1, openModal1, closeModal1] = useModal(false)

  const [product, setProduct] = useState([])
  useEffect(() => {
    peticionGet()
  }, [])


  const peticionGet = async () => {

    const res = await axios.get(url)
    setProduct(res.data)
    console.log(res)
  }

    return (
      <div className='col-md-12'>
        <table className='table'>
          <thead className='bg-primary text-white'>
            <tr>
              <th>ID</th>
              <th>DESCRIPCIÓN</th>
              <th>PRECIO</th>
              <th>ACCIÓN</th>
            </tr>
          </thead>
          <tbody>
            {product.map ((product) => (
              <tr>
                  <th>{product.codigo}</th>
                  <td>{product.descripcion}</td>
                  <td>{product.precio}</td>
                  <td width='30%'><a className="btn btn-primary" onClick={openModal1}>Editar</a> &nbsp;&nbsp;
                    <button type="button" className="btn btn-danger">Eliminar</button></td>
                </tr>
            ))}
            <FormEdit isOpen={isOpenModal1} closeModal={closeModal1}></FormEdit>

          </tbody>
        </table>
      </div>
    );
}

export default TablaMateriales;
