import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useState } from 'react';
import axios from 'axios';

const url = "http://localhost:3001/addInventario"

function FormAdd() {

    const [id, setId] = useState('')
    const [descripcion, setDescripcion] = useState('')
    const [precio, setPrecio] = useState('')

    const add = async (e) => {
        e.preventDefault()
        try {
            const resp = await axios.post(url, {id, descripcion, precio});
            console.log(resp.data);
        } catch (err) {
            // Handle Error Here
            console.error(err);
        }
    }

        return (

            <div className=" mx-auto mt-5" style={{width: "30rem"}}>
                <div className="card-body p-5 ">
                    <br />
                    <h3 className="card-title text-info mx-5"><b>Agregar Un Producto</b></h3><br />
                    <form className="form-group" onSubmit={add}>
                        <label className="ml-2">Código del nuevo producto:</label>
                        <p><input type="text" placeholder="Código" className="form-control " name="codigo" value={id} required onChange={(e) => setId(e.target.value)} ></input></p>
                        <label className="ml-2">Descripción del nuevo producto</label>
                        <p><input type="text" placeholder="Descripción" className="form-control" name="descripcion" value={descripcion} required onChange={(e) => setDescripcion(e.target.value)} ></input></p>
                        <label className="ml-2">Precio del nuevo producto:</label>
                        <p><input type="text" placeholder="Precio" className="form-control" name="precio" value={precio} required onChange={(e) => setPrecio(e.target.value)} ></input></p>
                        <br />
                        <button type="submit" className="btn btn-success float-left px-5">Agregar</button> 
                        <a href="/getInventario"  className="btn btn-danger float-right px-5">Cancelar</a>
                    </form>
                </div>
            </div>
        );
};

export default FormAdd;
