import React, {Component,} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';

const url = "http://localhost:3001/addInventario"

class Formulario extends Component{

    constructor(props){
        super(props)

        this.state = {
            busqueda: ""
        }

    }

    changeHandler = (e) => {
        this.setState({[e.target.name]: e.target.value})
    }

    submitHandler = e => {
        e.preventDefault()
        axios.post(url, this.state)
        .then(response => {
          alert("Producto Agregado Exitosamente")
          window.location.reload(true)
        })
        .catch(error =>{
            alert("error")
        })
        console.log(this.state)
    }


    render() {

        const   {busqueda} = this.state

        return(
            <div className='container '>
                <form className="row" onSubmit={this.submitHandler}>
                    <div className="col-8">
                    <input type="text" placeholder="Escribe algo para buscar" className="form-control" name="busqueda" value={busqueda} required onChange={this.changeHandler} ></input>
                    </div>
                    <button type="submit" className="btn btn-info ">Buscar</button>
                    <a href="/agregar" className="btn btn-success mx-4">Agregar</a>
                </form>
                </div>

        )
    }

}

export default Formulario;
