import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { useState } from 'react';

const url = "http://localhost:3001/editInventario"
const urlGet = "http://localhost:3001/addInventario"

const FormEdit = ({children, openModal, closeModal}) => {


    const [state, setState] = useState({
        codigo: "",
        descripcion: "",
        precio: ""
    })

    const id = useParams()

    const handleChange = (e) => {

        setState({
            ...state,
            [e.target.name]: e.target.value
        })
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        const productoData = {
            codigo: state.codigo,
            descripcion: state.descripcion,
            precio: state.precio,
        };

        axios.put(url, productoData)
        .then((res) => {
            alert("Producto Editado Exitosamente")
                window.location.replace("/getInventario");
            })
            .catch(error => {
                alert("error")
            })
        console.log(productoData)
    }

    const getProduct = async () => {
        const res = await axios.get(urlGet+id)
        console.log(res.data)
    }

    return(
        <div></div>
    );

}

export default FormEdit;
