import './App.css';

import 'bootstrap/dist/css/bootstrap.min.css';

import { Component } from 'react';
import axios from 'axios';

const url = "http://localhost:3001/getInventario"

class App extends Component{

state = {
  data:[]
}

peticionGet = () => {
  axios.get(url).then(response => {
    this.setState({data: response.data});
  })
}


componentDidMount(){
  this.peticionGet();
}

    render(){
    return (
    <div className='container col-6'>
      <br></br>
        <center>
          <h2 className=''><b>TABLA DE INVENTARIO</b></h2>
        <br></br>
        <button type="button" className="btn btn-success col-2">Agregar</button>
        </center>
      {/* <br></br>

        <form>
          <div className="form-group col-4 mx-auto">
            <label for="exampleInputEmail1">ID</label>
            <input type="number" className="form-control" id="id" placeholder="-" />

            <label for="exampleInputEmail1">DESCRIPCIÓN</label>
            <input type="text" className="form-control" id="descripcion" placeholder="-" />

            <label for="exampleInputEmail1">PRECIO</label>
            <input type="number" className="form-control" id="precio" placeholder="-" />
          </div>
        </form> */}

      <br></br>
      <center>
      <table className='table'>
        <thead className='bg-primary text-white'>
          <tr>
            <th>ID</th>
            <th>DESCRIPCIÓN</th>
            <th>PRECIO</th>
            <th>ACCIÓN</th>
          </tr>
        </thead>
        <tbody>
          {this.state.data.map(users=>{
            return(
            <tr>
              <th>{users.codigo}</th>
              <td>{users.descripcion}</td>
              <td>{users.precio}</td>
              <td width='30%'><button type="button" className="btn btn-primary">Editar</button> &nbsp;&nbsp;
              <button type="button" className="btn btn-danger">Eliminar</button></td>
            </tr>
            )
          })}
        </tbody>
        </table>
      </center>
    </div>
  );
}
}

export default App;
