import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import { Link, useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';

const url = "http://localhost:3001/modInventario"
const urlGet = "http://localhost:3001/getInventario?codigo="

const FormDelete = ({ children }) => {


    const [product, setProduct] = useState([])


    useEffect(() => {
        getProductById()
    }, [])

    const { codigo } = useParams()


    const handleSubmit = (e) => {
        e.preventDefault()
        const productoData = {
            codigo: document.getElementById("codigo").value,
            descripcion: document.getElementById("descripcion").value,
            precio: document.getElementById("precio").value,
        };

        axios.put(url, productoData)
            .then((res) => {
                alert("Producto Editado Exitosamente")
                window.location.replace("/getInventario");
            })
            .catch(error => {
                alert("error")
            })
        console.log(productoData)
    }

    const getProductById = async () => {
        const res = await axios.get(urlGet + codigo)
        setProduct(res.data)
    }

    return (
        <div> <div className="modal is-open">
            <div className=" mx-auto mt-5" style={{ width: "60rem" }}>
                <div className="card-body p-5 bg-white">
                    <h3 className="card-title text-danger mx-5"><b>ELiminar Producto</b></h3><br />
                    {product.map((product) => (
                        <form className="form-group row">
                            <table>
                                <tr>
                                    <td>
                                        <label className="ml-2">Código:</label>
                                    </td>
                                    <td>
                                    <label className="">Descripción:</label>
                                    </td>
                                    <td>
                                    <label className="ml-2">Precio:</label>
                                    </td>
                                </tr>
                                <tr>
                                    <td>
                                        <input type="text" placeholder="Código" className="form-control " id='codigo' value={product.codigo} readOnly></input>
                                    </td>
                                    <td>
                                    <input type="text" className="form-control" id="descripcion" defaultValue={product.descripcion} readOnly></input>
                                    </td>
                                    <td>
                                    <input type="text" className="form-control" id="precio" defaultValue={product.precio} readOnly></input>
                                    </td>
                                </tr>
                            </table>
                            <br />
                            <button type="submit" className="btn btn-success float-left px-5">Agregar</button>
                            <Link className="btn btn-danger float-right px-5" to="/getInventario">Cancelar</Link>
                        </form>
                    ))}
                </div>
            </div>
        </div></div>
    );

}

export default FormDelete;
