import 'bootstrap/dist/css/bootstrap.min.css';
import React, { Component } from 'react';
import axios from 'axios';

const url = "http://localhost:3001/addInventario"

class Modal extends Component {

    constructor(props) {
        super(props)

        this.state = {
            codigo: '',
            descripcion: '',
            precio: ''
        }
    }

    changeHandler = (e) => {
        this.setState({ [e.target.name]: e.target.value })
    }

    submitHandler = e => {
        e.preventDefault()
        axios.post(url, this.state)
            .then(response => {
                alert("Producto Agregado Exitosamente")
                window.location.href("/getInventario")
            })
            .catch(error => {
                alert("error")
            })
        console.log(this.state)
    }


    render() {

        const   {codigo, descripcion, precio} = this.state

        return (

            <div className=" mx-auto mt-5" style={{width: "30rem"}}>
                <div className="card-body p-5 ">
                    <br />
                    <h3 className="card-title text-info mx-5"><b>Agregar Un Producto</b></h3><br />
                    <form className="form-group" onSubmit={this.submitHandler}>
                        <label className="ml-2">C贸digo del nuevo producto:</label>
                        <p><input type="text" placeholder="C贸digo" className="form-control " name="codigo" value={codigo} required onChange={this.changeHandler} ></input></p>
                        <label className="ml-2">Descripci贸n del nuevo producto</label>
                        <p><input type="text" placeholder="Descripci贸n" className="form-control" name="descripcion" value={descripcion} required onChange={this.changeHandler} ></input></p>
                        <label className="ml-2">Precio del nuevo producto:</label>
                        <p><input type="text" placeholder="Precio" className="form-control" name="precio" value={precio} required onChange={this.changeHandler} ></input></p>
                        <br />
                        <button type="submit" className="btn btn-success float-left px-5">Agregar</button> 
                        <a href="/getInventario"  className="btn btn-danger float-right px-5">Cancelar</a>
                    </form>
                </div>
            </div>
        );
    }
};

export default Modal;
