import React, {Fragment, useState} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const Formulario = () => {


    const [datos, setDatos] = useState({
        codigo: '',
        descripcion: '',
        precio: ''
    })

    const handleInputChange = (event) => {
        // console.log(event.target.name)
        // console.log(event.target.value)
        setDatos({
            ...datos,
            [event.target.name] : event.target.value
        })
    }

    const enviarDatos = (event) => {
        event.preventDefault()
        console.log('enviando datos...' + datos.codigo + ' ' + datos.descripcion)
    }

    return (
        <Fragment>
            <form className="row" onSubmit={enviarDatos}>
                <div className="col-3">
                    <input type="text" placeholder="Código" className="form-control" onChange={handleInputChange} name="codigo"></input>
                </div>
                <div className="col-3">
                    <input type="text" placeholder="Descripción" className="form-control" onChange={handleInputChange} name="descripcion"></input>
                </div>
                <div className="col-3">
                    <input type="text" placeholder="Precio" className="form-control" onChange={handleInputChange} name="precio"></input>
                </div>
                <button type="submit" className="btn btn-success">Enviar</button>
            </form>
        </Fragment>
    );
}

export default Formulario;
