import 'bootstrap/dist/css/bootstrap.min.css';

import { useEffect, useState } from 'react';
import axios from 'axios';
import { useModal } from '../Hooks/useModal';
import FormAdd from './FormAdd';
import { Link } from 'react-router-dom';

const url = "http://localhost:3001/getInventario"

const TablaMateriales = () => {

  const [isOpenModal1, openModal1, closeModal1] = useModal(false)

  const [product, setProduct] = useState([])
  useEffect(() => {
    peticionGet()
  }, [])


  const peticionGet = async () => {

    const res = await axios.get(url)
    setProduct(res.data)
    console.log(res)
  }

  return (
    <div className='col-md-12'>
      <table className=" col-12 ">
        <tr>
          <td align="center">
            <h3 className="text-primary"><b>INVENTARIO</b></h3>
          </td>
          <td align="center">
            <a className="btn btn-success mx-3" onClick={openModal1}>Agregar</a>
          </td>
        </tr>
      </table>
      <br />
      <table className="table table-hover " align="center">
        <thead className='bg-success text-white'>
          <tr align="center">
            <th><b>ID</b></th>
            <th><b>DESCRIPCIÓN</b></th>
            <th><b>PRECIO</b></th>
            <th><b>ACCIÓN</b></th>
          </tr>
        </thead>
        <tbody>
          {product.map((product) => (
            <tr>
              <td align='center'><b>{product.codigo}</b></td>
              <td>{product.descripcion}</td>
              <td>{product.precio}</td>
              <td width='30%'><Link className="btn btn-primary" to={`/getInventario/${product.codigo}`}>Editar</Link> &nbsp;&nbsp;
                <Link to={`/eliminar/${product.codigo}`} className="btn btn-danger">Eliminar</Link></td>
            </tr>
          ))}

        </tbody>
      </table>
      <FormAdd isOpen={isOpenModal1} closeModal={closeModal1}></FormAdd>
    </div>
  );
}

export default TablaMateriales;
