import React, {Component, useState} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { render } from 'react-dom';

class Formulario extends Component{

    constructor(props){
        super(props)

        this.state = {
            codigo: '',
            descripcion: '',
            precio: ''
        }

    }

    changeHandler = (e) => {
        this.setState({[e.target.name]: e.target.value})
    }

    submitHandler = e => {
        e.preventDefault()
        console.log(this.state)
    }


    render() {

        const   {codigo, descripcion, precio} = this.state

        return(
                <form className="row" onSubmit={this.submitHandler}>
                    <div className="col-3">
                        <input type="text" placeholder="Código" className="form-control" onChange={this.changeHandler} name="codigo"></input>
                    </div>
                    <div className="col-3">
                        <input type="text" placeholder="Descripción" className="form-control" onChange={this.changeHandler} name="descripcion"></input>
                    </div>
                    <div className="col-3">
                        <input type="text" placeholder="Precio" className="form-control" onChange={this.changeHandler} name="precio"></input>
                    </div>
                    <button type="submit" className="btn btn-success">Enviar</button>
                </form>
        )
    }

}

export default Formulario;
