import 'bootstrap/dist/css/bootstrap.min.css';

import { Component, useEffect, useState } from 'react';
import axios from 'axios';

const url = "http://localhost:3001/getInventario"

const TablaMateriales = () => {

  const [product, setProduct] = useState([])
  useEffect(() => {
    peticionGet()
  }, [])


  const peticionGet = async () => {

    const res = await axios.get(url)
    setProduct(res.data)
    console.log(res)
  }

    return (
      <div className='col-md-12'>
        <table className='table'>
          <thead className='bg-primary text-white'>
            <tr>
              <th>ID</th>
              <th>DESCRIPCIÓN</th>
              <th>PRECIO</th>
              <th>ACCIÓN</th>
            </tr>
          </thead>
          <tbody>
            {product.map ((product) => {
              <tr>
                  <th>{product.codigo}</th>
                  <td>{product.descripcion}</td>
                  <td>{product.precio}</td>
                  <td width='30%'><a href="/editar" className="btn btn-primary">Editar</a> &nbsp;&nbsp;
                    <button type="button" className="btn btn-danger">Eliminar</button></td>
                </tr>
            })}

          </tbody>
        </table>
      </div>
    );
}

export default TablaMateriales;
