import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import { Link, useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';

const url = "http://localhost:3001/modInventario"
const urlGet = "http://localhost:3001/getInventario?codigo="

const FormEdit = ({children}) => {

    const [state, setState] = useState({
        codigo: "",
        descripcion: "",
        precio: ""
    })

    const [product, setProduct] = useState([])

    useEffect(() => {
        getProductById()
      }, [])

    const {id} = useParams()
    let descripcion = ""
    let precio = ""

    const handleChange = (e) => {

        setState({
            ...state,
            [e.target.name]: e.target.value
        })
    }

    const handleSubmit = (e) => {
        e.preventDefault()

        axios.put(url, product)
        .then((res) => {
            alert("Producto Editado Exitosamente")
                window.location.replace("/getInventario");
            })
            .catch(error => {
                alert("error")
            })
        console.log(product)
    }

    const getProductById = async () => {
        const res = await axios.get(urlGet + id)
        setProduct(res.data)
        //console.log("****************")
        //console.log(res)
    }

    return(
        <div> <div className="modal is-open">
        <div className=" mx-auto mt-5" style={{width: "30rem"}}>
            <div className="card-body p-5 bg-white">
                <h3 className="card-title text-info mx-5"><b>Editar Productos</b></h3><br />
                {product.map ((product) => (
                <form className="form-group" onSubmit={handleSubmit}>
                    <label className="ml-2">Código del producto:</label>
                    <p><input type="text" placeholder="Código" className="form-control " name="codigo" value={id} readOnly></input></p>
                    <label className="ml-2">Descripción del producto:</label>
                    <p><input type="text" placeholder={product.descripcion} className="form-control" name="descripcion" value={descripcion} onChange={handleChange}></input></p>
                    <label className="ml-2">Precio del producto:</label>
                    <p><input type="text" placeholder={product.precio} className="form-control" name="precio"  value={precio} onChange={handleChange}></input></p>
                    <br />
                    <button type="submit" className="btn btn-success float-left px-5">Agregar</button> 
                    <Link className="btn btn-danger float-right px-5" to="/getInventario">Cancelar</Link>
                </form>
                ))}
            </div>
        </div>
        </div></div>
    );

}

export default FormEdit;
