import React, {Fragment, useState} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const Formulario = () => {


    const [datos, setDatos] = useState({
        nombre: '',
        apellido: ''
    })

    const handleInputChange = (event) => {
        // console.log(event.target.name)
        // console.log(event.target.value)
        setDatos({
            ...datos,
            [event.target.name] : event.target.value
        })
    }

    const enviarDatos = (event) => {
        event.preventDefault()
        console.log('enviando datos...' + datos.nombre + ' ' + datos.apellido)
    }

    return (
        <Fragment>
            <center>
            <form className="row mx-auto" onSubmit={enviarDatos}>
                <div className="col-3">
                    <input type="text" placeholder="Código" className="form-control" onChange={handleInputChange} name="nombre"></input>
                </div>
                <div className="col-3">
                    <input type="text" placeholder="Descripción" className="form-control" onChange={handleInputChange} name="apellido"></input>
                </div>
                <div className="col-3">
                    <input type="text" placeholder="Precio" className="form-control" onChange={handleInputChange} name="apellido"></input>
                </div>
                <button type="submit" className="btn btn-primary">Enviar</button>
            </form>
            </center>

        </Fragment>
    );
}

export default Formulario;
