import 'bootstrap/dist/css/bootstrap.min.css';
import { Component, useState } from 'react';
import Modal from './Modal';


class Menu extends Component {

    constructor () {
        super();
        this.state = {
          showModal: false
        };

        this.handleOpenModal = this.handleOpenModal.bind(this);
        this.handleCloseModal = this.handleCloseModal.bind(this);

    }

    handleOpenModal () {
        this.setState({ showModal: true });
      }

      handleCloseModal () {
        this.setState({ showModal: false });
      }

        render (){


    return (
            <div className="container mt-5 mx-auto" >
                <label for="exampleInputEmail1" className="form-label h1 w-100 text-center">Menu</label>
                <br/><br/>
                <center>
                    <a href='/getInventario' className="btn btn-primary px-5 pb-0" ><h5>VER INVENTARIO</h5></a>
                    <br /> <br /> 
                    <button className="btn btn-primary px-5 pb-0" onClick={this.handleOpenModal}>modal</button>
                </center>

                <Modal 
                    isOpen={this.state.showModal}
                ><button onClick={this.handleCloseModal}>Close Modal</button>
                    </Modal>

            </div>
    );
  }
}

  export default Menu
