import 'bootstrap/dist/css/bootstrap.min.css';
import React, { Component } from 'react';
import axios from 'axios';

const url = "http://localhost:3001/addInventario"

class Modal extends Component {

    constructor(props) {
        super(props)

        this.state = {
            codigo: '',
            descripcion: '',
            precio: ''
        }

    }

    changeHandler = (e) => {
        this.setState({ [e.target.name]: e.target.value })
    }

    submitHandler = e => {
        e.preventDefault()
        axios.post(url, this.state)
            .then(response => {
                alert("Producto Agregado Exitosamente")
                window.location.reload(true)
            })
            .catch(error => {
                alert("error")
            })
        console.log(this.state)
    }


    render() {

        const   {codigo, descripcion, precio} = this.state

        return (

            <div className="card border border-dark mx-auto" style={{width: "30rem"}}>
                <div className="card-body">
                    <h5 className="card-title">Agregar Un Nuevo Producto:</h5><br />
                    <form className="form-group" onSubmit={this.submitHandler}>
                        <div className="col-3 ">
                            <input type="text" placeholder="CÃ³digo" className="form-control" name="codigo" value={codigo} required onChange={this.changeHandler} ></input>
                        </div>
                        <div className="col-3 ">
                            <input type="text" placeholder="DescripciÃ³n" className="form-control" name="descripcion" value={descripcion} required onChange={this.changeHandler} ></input>
                        </div>
                        <div className="col-3 ">
                            <input type="text" placeholder="Precio" className="form-control" name="precio" value={precio} required onChange={this.changeHandler} ></input>
                        </div>
                        <button type="submit" className="btn btn-success mx-auto">Agregar</button>
                    </form>
                    <h6 className="card-subtitle mb-2 text-muted">Card subtitle</h6>
                    <p className="card-text">Some quick example text to build</p>
                    <button className="btn btn-primary">Enviar</button>
                </div>
            </div>
        );
    }
};

export default Modal;
