import './App.css';

import 'bootstrap/dist/css/bootstrap.min.css';

import { Component } from 'react';
import axios from 'axios';

const url = "http://localhost:3001/getInventario"

class App extends Component{

state = {
  data:[]
}

peticionGet = () => {
  axios.get(url).then(response => {
    this.setState({data: response.data});
  })
}


componentDidMount(){
  this.peticionGet();
}

    render(){
    return (
    <div className='container col-6'>
      <br></br>
        <h2 className=''><b>TABLA DE INVENTARIO</b></h2>
      <br></br>

        <form>
          <div className="form-group col-4 mx-auto">
            <label for="exampleInputEmail1">Email address</label>
            <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email" />
            <small id="emailHelp" className="form-text text-muted">We'll never share your email with anyone else.</small>
          </div>
        </form>

      <br></br>
      <center>
      <table className='table'>
        <thead className='bg-primary text-white'>
          <tr>
            <th>ID</th>
            <th>DESCRIPCIÓN</th>
            <th>PRECIO</th>
            <th>ACCIÓN</th>
          </tr>
        </thead>
        <tbody>
          {this.state.data.map(users=>{
            return(
            <tr>
              <th>{users.codigo}</th>
              <td>{users.descripcion}</td>
              <td>{users.precio}</td>
              <td width='30%'><button type="button" className="btn btn-primary">Editar</button> &nbsp;&nbsp;
              <button type="button" className="btn btn-danger">Eliminar</button></td>
            </tr>
            )
          })}
        </tbody>
        </table>
      </center>
    </div>
  );
}
}

export default App;
